mapper.update:
update orderList
set floor = #{floor}
where orderNo = #{orderNo}

mapper.select:
select itemName, size, color, count, price, imgSaveName, imageSize
from detailList
where orderNo = #{orderNo}


	select * from orders
	WHERE date(date) >= date(DATE_SUB(now(), INTERVAL 3 month)) and date(date) <= date(now())
	and memNo=#{memNo}
	GROUP BY orderNo
	ORDER BY date ASC;


	select * from orders
	WHERE date(date) >= date(DATE_SUB(now(), INTERVAL 6 month)) and date(date) <= date(now())
	and memNo=#{memNo}
	GROUP BY orderNo
	ORDER BY date ASC;

select * 
FROM orders
where memNo=#{list[0]}
and	date
between #{list[1]} and #{list[2]}
GROUP BY orderNo
ORDER BY date ASC;

